package hainu.com.adultloan.activity;import android.os.Bundle;import android.view.View;import android.widget.RelativeLayout;import android.widget.TextView;import hainu.com.adultloan.R;import hainu.com.adultloan.application.MyApplication;public class ApplySuccessActivity extends BaseActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_apply_success);        RelativeLayout contact_qq = (RelativeLayout) findViewById(R.id.contact_qq);        TextView qq = (TextView) findViewById(R.id.qq);        String qqContactInfo = MyApplication.sharedPreferences.getString("qqContactInfo", "");        if (!qqContactInfo.equals("")){            contact_qq.setVisibility(View.VISIBLE);            qq.setText(qqContactInfo);        }    }    public void knowIt(View view){        finish();    }}